import React, { useState } from "react";

const CreateList = () => {
  const [title, setTitle] = useState("");
  const [designer, setDesigner] = useState("");

  const handleAddingList = (e) => {
    e.preventDefault();
    if (title === "") {
      alert("Enter your Post title");
    } 
    else if (designer === "") {
      alert("Enter your Designer name");
    } 
    else {
      const postDetails = { title, designer };
      fetch("http://localhost:5000/posts", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(postDetails),
      })
        .then(() => {
          alert("New post added");
          setTitle(" ");
          setDesigner(" ");
          window.location.reload();
        })
    }
  };

  return (

    <div className="header row justify-content-center py-5">
      <div className="col-12">
            <h4>Poster and Designer </h4>
      </div>
      <div className="col-10">
        <form onSubmit={handleAddingList}>
          <label>Poster title</label>
          <input
            type="text"
            placeholder="Enter Post title"
            value={title}
            onChange={(e) => setTitle(e.target.value.trimStart())}
          />
          <label>Designer name</label>
          <input
            type="text"
            placeholder="Enter Designer name"
            value={designer}
            onChange={(e) => setDesigner(e.target.value.trimStart())}
          />
          <button type="submit" className="btn btn-outline-dark">
            Add List
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateList;
