import React from 'react'
import { useState } from 'react'
import SingleTodo from './singleTodo'

const Home = ({todos,handleDelete,handleUpdate,todo}) => {
  const [list,setList]=useState('')

  const handleAddingList=(e)=>{
      e.preventDefault()
      // console.log("hello");
      fetch('http://localhost:8000/posts', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(todo)
    }).then(() => {
      console.log('new blog added');
    })
  }
  return (
    <div className="container">

      <div className='header row justify-content-center my-5'>
       <div className='col-7'>
        <form onSubmit={handleAddingList}>
        <input type='text'  placeholder='Enter Lists' value={list} onChange={(e)=>setList(e.target.value)} />
        <button type='submit' className='btn btn-dark' >Add List</button>
        </form>
       </div>
      </div>

    <table className='table table-striped'>
      <thead>
       <tr>
        <th scope='col'>ID</th>
        <th scope='col'>Title</th>
        {/* <th scope='col'>Status</th> */}
        <th scope='col'>Update</th>
        <th scope='col'>Delete</th>
       </tr>
      </thead>
        {todos && todos.map((todo)=>{
          return(
           <SingleTodo  todo={todo} key={todo.id} handleDelete={handleDelete} handleUpdate={handleUpdate} />
          )
        })}
    </table>


</div>
  )
}

export default Home
