import React, { useState } from 'react'

const CreateList = () => {
    const [title,setTitle]=useState('')
    const [author,setAuthor]=useState('')

    const handleAddingList=(e)=>{
        e.preventDefault();

        if(title === '' && author === ''){
            alert("Enter post title  and author's name")
        }
        else if(title ===''){
          alert('Enter your post title')
        }
        else if(author ===''){
          alert('Enter author name')
        }
        else{
        const postDetails ={title,author}
        fetch('http://localhost:5000/posts', {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(postDetails)
        }).then(() => {
        console.log('new post added');
        setTitle('');
        setAuthor('');
        window.location.reload();
        })
    }

    }
  return (
    <div className='header row justify-content-center py-5'>
       <div className='col-10'>
        <form onSubmit={handleAddingList}>
        <input type='text'  placeholder="Enter Post title" value={title} onChange={(e)=>setTitle(e.target.value.trimStart())} />
        <input type='text'  placeholder="Enter Author's name" value={author} onChange={(e)=>setAuthor(e.target.value.trimStart())} />
        <button type='submit' className='btn btn-outline-dark' >Add List</button>
        </form>
       </div>
      </div>
  )
}

export default CreateList
