import { useEffect, useState } from 'react';
import './App.css';
// import Todo from './components/todo';

function App() {
  const [todos,setTodos]=useState()

  useEffect(()=>{
    const url = 'https://jsonplaceholder.typicode.com/todos';

    fetch(url)
    .then((res)=> {
      return res.json()
    })
    .then((data)=> {
        // console.log(data)
        setTodos(data)
    })
  },[]);

  return (
    <div className="container">
      {/* <Todo /> */}

            <table className='table '>
              <thead>
               <tr>
                <th scope='col'>ID</th>
                <th scope='col'>Title</th>
                <th scope='col'>Status</th>
                <th scope='col'>Update</th>
                <th scope='col'>Delete</th>
               </tr>
              </thead>
                {todos && todos.map((todo)=>{
                  return(
                    <tbody key={todo.id} className='table-striped'>
                      <tr>
                        <td>{todo.id}</td>
                        <td>{todo.title}</td>
                        <td>{todo.completed? 
                          <span className='p-2 rounded crud bg-success'><i className='bi bi-check-circle-fill me-2'></i>Completed</span> : 
                          <span className='p-2 rounded crud bg-warning'><i className='bi bi-x-circle-fill me-2'></i>Pending</span> }
                        </td>
                        <td>
                          <span className='p-2 rounded crud bg-primary'><i className='bi bi-pencil-square me-2'></i> Edit</span>
                        </td>
                        <td>
                          <span className='p-2 rounded crud bg-danger'><i className='bi bi-trash3-fill me-2'></i> Delete</span>
                        </td>
                      </tr>
                    </tbody>
                  )
                })}
            </table>


    </div>
  );
}

export default App;
