import React from "react";
// import { Link } from "react-router-dom";

const SingleTodo = ({ posts,handleDelete,handleEdit }) => {
  return posts.length > 0 ? (
    <table className="table table-dark">
      <thead>
        <tr>
          <th scope="col">ID no</th>
          <th scope="col">Post title</th>
          <th scope="col">Designer</th>
          <th scope="col">Edit</th>
          <th scope="col">Delete</th>
        </tr>
      </thead>
      {posts.map((post, index) => {
        return (
          <tbody key={post.id}>
            <tr className="align-items-center">
              <td>{index + 1}</td>
              <td>{post.title}</td>
              <td>{post.designer}</td>
              <td>
                {/* <Link to={`/post/edit_page/${post.id}`}> */}
                <button className="btn btn-primary" onClick={handleEdit(post.id)}>
                  <i className="bi bi-pencil-square "></i>
                </button>
                {/* </Link> */}
              </td>
              <td>
                <button className="btn btn-danger" onClick={()=>handleDelete(post.id)}>
                  <i className="bi bi-trash3-fill "></i>
                </button>
              </td>
            </tr>
          </tbody>
        );
      })}
    </table>
  ) : (
    <div className="notfound ">No Data Found</div>
  );
};

export default SingleTodo;
