import SingleTodo from "./singleTodo";

const Home = () => {
  const [posts, setPosts] = useState(null);
  // const postid = useParams();
  const navigate= useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5000/posts`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setPosts(data);
      });
  }, []);

