    <div className="container">
      <h1 className="heading">Poster and Designer List</h1>
      <h4 className="addingList ">
        <span className="me-3">Add Your Poster and Designer List</span>
        <Link to={'/post/create_page'}>
        <button className="btn btn-success fw-bold">Add List </button>
        </Link>
      </h4>
      {posts && <SingleTodo posts={posts} handleDelete={handleDelete} handleEdit={handleEdit} /> }
    </div>
  );
};

export default Home;
