import React from 'react'
import { useState } from 'react'
import SingleTodo from './singleTodo'

const Home = ({todos,handleDelete,handleUpdate}) => {
  const [list,setList]=useState('')
  return (
    <div className="container">

      <div className='header row justify-content-center my-5'>
       <div className='col-7'>
       <input type='text'  placeholder='Enter Lists' value={list} onChange={(e)=>setList(e.target.value)} />
        <button className='btn btn-dark'>Add List</button>
       </div>
      </div>

    <table className='table table-striped'>
      <thead>
       <tr>
        <th scope='col'>ID</th>
        <th scope='col'>Title</th>
        {/* <th scope='col'>Status</th> */}
        <th scope='col'>Update</th>
        <th scope='col'>Delete</th>
       </tr>
      </thead>
        {todos && todos.map((todo)=>{
          return(
           <SingleTodo  todo={todo} key={todo.id} handleDelete={handleDelete} handleUpdate={handleUpdate} />
          )
        })}
    </table>


</div>
  )
}

export default Home
