import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SingleTodo from "./singleTodo";
import axios from 'axios'

const Home = () => {
  const [books, setBooks] = useState([]);
  const navigate= useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:8000/posts/`)
      .then((resp) => {
        setBooks(resp.data);
      });
  }, []);

  const handleDelete=(id)=>{
      if(window.confirm('Do you want to remove this ?')){
        axios.delete("http://localhost:8000/posts/" + id,)
        .then((resp) => {
          console.log(resp);
          window.location.reload()
        });
      }
    };

  const handleEdit=(id)=>{
    navigate('/post/edit_page/' + id)
  };


  return (
    <div className="container">
      <h1 className="heading">Books Listing  </h1>
      <h4 className="addingList ">
        <span className="me-3">Add Your Book and Author List</span>
        <Link to={'/post/create_page'}>
        <button className="btn btn-success fw-bold">Add List </button>
        </Link>
      </h4>
      {books && <SingleTodo books={books} handleDelete={handleDelete} handleEdit={handleEdit} /> }
    </div>
  );
};

export default Home;
