import React, { useState } from 'react'

const CreateList = () => {
    const [title,setTitle]=useState('')

    const handleAddingList=(e)=>{
        e.preventDefault();
        // console.log("hello");
        if(title === ''){
            alert('Enter Something')
        }else{
        const todo ={title}

        fetch('http://localhost:8000/posts', {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(todo)
        }).then(() => {
        console.log('new blog added');
        window.location.reload()
        })
    }

    }
  return (
    <div className='header row justify-content-center my-5'>
       <div className='col-7'>
        <form onSubmit={handleAddingList}>
        <input type='text'  placeholder='Enter Lists' value={title} onChange={(e)=>setTitle(e.target.value)} />
        <button type='submit' className='btn btn-dark' >Add List</button>
        </form>
       </div>
      </div>
  )
}

export default CreateList
