import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const navigate =useNavigate();

const EditList = ({handleAddingList}) => {
    const [idno,IdChange]=useState('');
    const [title, TitleChange] = useState('');
    const [designer, DesignerChange] = useState('');

    const {id} =useParams();

  useEffect(() => {
    fetch(`http://localhost:5000/posts/${id}`)
      .then((res) => {
           return res.json()
      })
      .then((resp) => {
            IdChange(resp.id)
            TitleChange(resp.title)
            DesignerChange(resp.designer)
            // console.log(resp);
      });
  }, [id]);

  const postDetails = {idno,title, designer };
  fetch(`http://localhost:5000/posts/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(postDetails),
  })
    .then(() => {
    //   alert("Edited successful");
      navigate(`/`)
    })

  return (
    <div className="header row justify-content-center py-5">
      <div className="col-12">
          <h2 className="text-center mb-4">Edit Poster and Designer </h2>
      </div>
      <div className="col-10">
        <form onSubmit={handleAddingList}>
           <label>ID</label>
           <input
            type="text"
            disabled
            readOnly
            value={idno}
          />
          <label>Poster title</label>
          <input
            type="text"
            placeholder="Enter Post title"
            value={title}
            onChange={(e) => TitleChange(e.target.value.trimStart())}
          />
          <label>Designer name</label>
          <input
            type="text"
            placeholder="Enter Designer name"
            value={designer}
            onChange={(e) => DesignerChange(e.target.value.trimStart())}
          />
          <button type="submit" className="btn btn-outline-dark">
            Edit List
          </button>
        </form>
      </div>
    </div>
  )
}

export default EditList
