import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const EditList = () => {
  const [idno, IdChange] = useState("");
  const [title, TitleChange] = useState("");
  const [author, AuthorChange] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:8000/posts/${id}`)
      .then((res) => {
        return res.json();
      })
      .then((resp) => {
        IdChange(resp.id);
        TitleChange(resp.title);
        AuthorChange(resp.author);
      });
  }, [id]);

  const handleEditingList = (e) => {
    e.preventDefault();
    const BookDetails = { idno, title, author };

    axios(`http://localhost:8000/posts/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(BookDetails),
    }).then(() => {
      console.log(e);
      alert("Edited successful");
        navigate('/')
    });
  };

  return (
    <div className="header row justify-content-center py-5">
      <div className="col-12">
        <h2 className="text-center mb-4">Edit Book and Author  </h2>
      </div>
      <div className="col-10">
        <form onSubmit={handleEditingList}>
          <label>ID</label>
          <input type="text" disabled readOnly value={idno} />
          <label>Book title</label>
          <input
            type="text"
            placeholder="Enter Book title"
            value={title}
            onChange={(e) => TitleChange(e.target.value.trimStart())}
          />
          <label>Author name</label>
          <input
            type="text"
            placeholder="Enter Author name"
            value={author}
            onChange={(e) => AuthorChange(e.target.value.trimStart())}
          />
          <button type="submit" className="btn btn-outline-dark">
            Edited 
          </button>
        </form>
      </div>
    </div>
  );
};

export default EditList;
