import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
// import { useNavigate } from 'react-router-dom';

const EditList = ({handleAddingList}) => {
    const [id,setIdChange]=useState();
    const [title, setTitle] = useState("");
    const [designer, setDesigner] = useState("");
    const postid =useParams();

  useEffect(() => {
    fetch(`http://localhost:5000/posts/${postid}`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
      });
  }, []);
  return (
    <div className="header row justify-content-center py-5">
      <div className="col-12">
          <h2 className="text-center mb-4">Edit Poster and Designer </h2>
      </div>
      <div className="col-10">
        <form onSubmit={handleAddingList}>
        <input
            type="text"
            disabled
            readOnly
            value={id}
            onChange={(e) => setIdChange(e.target.value.trimStart())}
          />
          <label>Poster title</label>
          <input
            type="text"
            placeholder="Enter Post title"
            value={title}
            onChange={(e) => setTitle(e.target.value.trimStart())}
          />
          <label>Designer name</label>
          <input
            type="text"
            placeholder="Enter Designer name"
            value={designer}
            onChange={(e) => setDesigner(e.target.value.trimStart())}
          />
          <button type="submit" className="btn btn-outline-dark">
            Edit List
          </button>
        </form>
      </div>
    </div>
  )
}

export default EditList
