import React from 'react'

const SingleTodo = ({todos,handleDelete,handleUpdate}) => {
  return (

    <table className='table table-striped'>
      <thead>
       <tr>
        <th scope='col'>ID</th>
        <th scope='col'>Title</th>
        <th scope='col'>Update</th>
        <th scope='col'>Delete</th>
       </tr>
      </thead>
        {todos && todos.map((todo,index)=>{
          return(
            <tbody key={todo.id}>
            <tr className='align-items-center'>
              <td>{index+1}</td>
              <td>{todo.title}</td>
              {/* <td>{todo.completed? 
                <button className='btn btn-success'><i className='bi bi-check-circle-fill me-2'></i>Completed</button> : 
                <button className='btn btn-warning'><i className='bi bi-x-circle-fill me-2'></i>Pending</button> }
              </td> */}
              <td>
                <button className='btn btn-primary' onClick={handleUpdate}><i className='bi bi-pencil-square me-2'></i> Edit</button>
              </td>
              <td>
                <button className='btn btn-danger' onClick={handleDelete}><i className='bi bi-trash3-fill me-2'></i> Delete</button>
              </td>
            </tr>
          </tbody>
          )
        })}
    </table>

  )
}

export default SingleTodo
