import React, { useState } from 'react'

const CreateList = () => {
    const [title,setTitle]=useState('')
    const [designer,setDesigner]=useState('')

    const handleAddingList=(e)=>{
        e.preventDefault();


         if(title ===''){
          alert('Enter your post title')
        }
        else if(designer ===''){
          alert('Enter post designer')
        }
        else{
        const postDetails ={title,designer}
        fetch('http://localhost:5000/posts', {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(postDetails)
        }).then(() => {
        console.log('new post added');
        setTitle('');
        setDesigner('');
        window.location.reload();
        })
    }

    }
  return (
    <div className='header row justify-content-center py-5'>
       <div className='col-10'>
        <form onSubmit={handleAddingList}>
        <input type='text'  placeholder="Enter Post title" value={title} onChange={(e)=>setTitle(e.target.value.trimStart())} />
        <input type='text'  placeholder="Enter Author's name" value={designer} onChange={(e)=>setDesigner(e.target.value.trimStart())} />
        <button type='submit' className='btn btn-outline-dark' >Add List</button>
        </form>
       </div>
      </div>
  )
}

export default CreateList
