import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {useNavigate} from 'react-router-dom'
import Loader from "./loader";
import SingleTodo from "./singleTodo";

const Home = () => {
  const [books, setBooks] = useState([]);
  const [isPending,setIsPending]=useState(true)
  const navigate= useNavigate();

  useEffect(() => {
    setTimeout(() => {
      fetch(`http://localhost:8000/posts`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setIsPending(false)
        setBooks(data);
      });
    }, 1000);
  }, []);

  const handleDelete=(id)=>{
      if(window.confirm('Do you want to remove this ?')){
        fetch("http://localhost:8000/posts/" + id, {
          method: "DELETE"
        }).then(() => {
          window.location.reload()
        });
      }
    };

  const handleEdit=(id)=>{
   setTimeout(() => {
    navigate('/post/edit_page/' + id)
   }, 1000);
  };


  return (
    <div className="container">
      {isPending && <Loader />}
      <h1 className="heading">Books Listing  </h1>
      <h4 className="addingList ">
        <span className="me-3">Add Your Book and Author List</span>
        <Link to={'/post/create_page'} ispending={isPending}>
        <button className="btn btn-success fw-bold">Add List </button>
        </Link>
      </h4>
      {books && <SingleTodo books={books} handleDelete={handleDelete} handleEdit={handleEdit} /> }
    </div>
  );
};

export default Home;
