import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import SingleTodo from "./singleTodo";
import api from "./api/api.jsx";

const Home = () => {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get(`/posts/`).then((resp) => {
      setBooks(resp.data);
    });
  }, []);

  const handleDelete = (id) => {
    if (window.confirm("Do you want to remove this ?")) {
      fetch(`http://localhost:8000/posts/${id}`, {
        method: "DELETE",
      }).then(() => {
        window.location.reload();
      });
    }
  };

  const handleCreate = () => {
    navigate(`/post/create_page`);
  };

  const handleEdit = (id) => {
    navigate(`/post/edit_page/${id}`);
  };

  return (
    <div className="container">
      <h1 className="heading">Books Listing </h1>
      <h4 className="addingList ">
        <span className="me-3">Add Your Book and Author List</span>
        <button className="btn btn-success fw-bold" onClick={handleCreate}>
          Add List
        </button>
      </h4>
      {books && (
        <SingleTodo
          books={books}
          handleDelete={handleDelete}
          handleEdit={handleEdit}
        />
      )}
    </div>
  );
};

export default Home;
